CREATE TABLE [dbo].[re_ruleset] (
	[nRuleSetID] [bigint] NOT NULL IDENTITY (1, 1),
	[strName] [nvarchar] (256) COLLATE Latin1_General_BIN NOT NULL,
	[nMajor] [bigint] NOT NULL,
	[nMinor] [bigint] NOT NULL,
	[nVersion] AS [nMajor] * 4294967296 + [nMinor],
	[strDescription] [nvarchar] (2048) NOT NULL,
	[strModifiedBy] [nvarchar] (256) NOT NULL,
	[dtModifiedTime] [datetime] NOT NULL,
	[nStatus] [tinyint] NOT NULL ,
	[imBRML] [image] NOT NULL
	CONSTRAINT re_ruleset_pk PRIMARY KEY(nRuleSetID)
) ON [PRIMARY] TEXTIMAGE_ON [PRIMARY]
GO

CREATE UNIQUE INDEX re_ruleset_unique_index_key ON [dbo].[re_ruleset](strName, nMajor, nMinor)
GO

